{"changed":true,"filter":false,"title":"sessions_controller.rb","tooltip":"/app/controllers/sessions_controller.rb","value":"class SessionsController < ApplicationController\n  def new\n  end\n  \n  def create\n    user = User.find_by(email: params[:session][:email].downcase)\n    if user && user.authenticate(params[:session][:password])\n      log_in user\n      params[:session][:remember_me] == '1' ? remember(user) : forget(user)\n      remember user\n      redirect_to user\n    else\n      flash.now[:danger] = 'Invalid email/password combination'\n      render 'new'\n    end\n  end\n  \n  def destroy\n    log_out\n    redirect_to root_url\n  end\nend\n","undoManager":{"mark":1,"position":3,"stack":[[{"start":{"row":7,"column":17},"end":{"row":8,"column":0},"action":"insert","lines":["",""],"id":112},{"start":{"row":8,"column":0},"end":{"row":8,"column":6},"action":"insert","lines":["      "]}],[{"start":{"row":8,"column":6},"end":{"row":8,"column":75},"action":"insert","lines":["params[:session][:remember_me] == '1' ? remember(user) : forget(user)"],"id":113}],[{"start":{"row":5,"column":4},"end":{"row":5,"column":5},"action":"insert","lines":["?"],"id":114}],[{"start":{"row":5,"column":4},"end":{"row":5,"column":5},"action":"remove","lines":["?"],"id":115}]]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":5,"column":4},"end":{"row":5,"column":4},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1525658543704}